				if (Player.statLife >= Player.statLifeMax2)
				{
					lifepotionregenpower = 0;
					lifepotionregen = 0;
					Player.statLife = Player.statLifeMax2;
				}
			}
			else
			{
				lifepotionregenpower = 0;
			}
			Player.maxMinions += skillMinionMax;
			StatModifier minionDamageMod =
			Player.GetDamage(DamageClass.Summon) = Player.GetDamage(DamageClass.Summon).CombineWith(new StatModifier(skillMinionDamage, 0));



			//Update player status bonus

			Player.GetDamage(DamageClass.Melee) = Player.GetDamage(DamageClass.Melee).CombineWith(new StatModifier(((float)str/600f)*1.1f -0.25f + skillMeleeDmg, 0));
			Player.GetCritChance(DamageClass.Melee) += (str/75) + (int)skillMeleeCrit;
			Player.GetDamage(DamageClass.Ranged) = Player.GetDamage(DamageClass.Ranged).CombineWith(new StatModifier(((float)dex/600f)*0.9f -0.25f + skillRangeDmg, 0));
			Player.GetCritChance(DamageClass.Ranged) += (dex/50) + (int)skillRangeCrit;
			Player.GetDamage(DamageClass.Magic) = Player.GetDamage(DamageClass.Magic).CombineWith(new StatModifier(((float)intel/400f)*0.6f -0.25f + skillMagicDmg, 0));
			Player.GetCritChance(DamageClass.Magic) += (intel/100) + (int)skillMagicCrit;
			//Player.jumpHeight+=(dex/20);
			Player.moveSpeed += (float)(dex/600f) + skillMoveSpeed;
