			//if(Player.whoAmI == Main.player[Player.whoAmI])
			Player.statDefense += skillDefense;

			if(skill80lev > 0 || skill131lev > 0) Player.statDefense = Player.statDefense/2;
			if(skill80lev > 0 && skill131lev > 0) Player.statDefense = 0;
			
			
			
		}
		
		
		
		
		
		
		
		
		
