					if(Main.player[Main.myPlayer].statManaMax2 < 0) Main.player[Main.myPlayer].statManaMax2 = 0;
					Main.player[Main.myPlayer].statMana = Main.player[Main.myPlayer].statManaMax2;
					statEnergyShield = skillEnergyShield;
					Main.player[Main.myPlayer].statLife = Main.player[Main.myPlayer].statLifeMax2;
					loaded = true;
				}
				
				
				
				
				//mana potion active
				if (manapotionregen > 0) 
				{
					Main.player[Main.myPlayer].statMana += manapotionregenpower;
					manapotionregen -= manapotionregenpower;
					if (Main.player[Main.myPlayer].statMana >= Main.player[Main.myPlayer].statManaMax2)
