		public override bool PreHurt(bool pvp, bool quiet, ref int damage, ref int hitDirection, ref bool crit, ref bool customDamage, ref bool playSound, ref bool genGore, ref PlayerDeathReason damageSource)
		{
			if (Main.player[Main.myPlayer].immune)
			{
				crit = true;
				return false;
			}
			if(Main.rand.Next(100) < skillDodge)
			{
				CombatText.NewText(new Rectangle((int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, Main.player[Main.myPlayer].width, Main.player[Main.myPlayer].height), new Color(50, 255, 50, 255), string.Concat("EVADE"), false);
				crit = true;
				Main.player[Main.myPlayer].immune = true;
				Main.player[Main.myPlayer].immuneTime = 40;
				if(Main.player[Main.myPlayer].longInvince) Main.player[Main.myPlayer].immuneTime +=40;
				SoundEngine.PlaySound(2, (int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, 8);
				return false;
			}
			if(lifebeforehurt == 0) lifebeforehurt = Main.player[Main.myPlayer].statLife;
			hurttime = 0;
			statEnergyShieldTimer = 0;
			
			
			//energy shield
			if(statEnergyShield > 0)
			{
				if(statEnergyShield > damage)
				{
					
					statEnergyShield -= damage;
					//crit = true;
					Main.player[Main.myPlayer].immune = true;
					Main.player[Main.myPlayer].immuneTime = 40;
					if(Main.player[Main.myPlayer].longInvince) Main.player[Main.myPlayer].immuneTime +=40;
					//if (!Main.player[Main.myPlayer].male)
					//{
						SoundEngine.PlaySound(20, (int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, 1);
					//}
					//else
					//{
					//	SoundEngine.PlaySound(1, (int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, 1);
					//}
					CombatText.NewText(new Rectangle((int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, Main.player[Main.myPlayer].width, Main.player[Main.myPlayer].height), new Color(255, 80, 90, 255), string.Concat((int)damage), crit);
					return false;
				}
				else
					damage -= statEnergyShield;
					int tempDmg = statEnergyShield;
					statEnergyShield = 0;
					if(skill80lev > 0)
					{
						double tempDamage = Main.CalculateDamagePlayersTake(damage, Main.player[Main.myPlayer].statDefense);
						damage =  (int)tempDamage;
						Main.player[Main.myPlayer].statMana -= damage;
						//LetHurt = true;
						Main.player[Main.myPlayer].immune = true;
						Main.player[Main.myPlayer].immuneTime = 40;
						if(Main.player[Main.myPlayer].longInvince) Main.player[Main.myPlayer].immuneTime +=40;
						//if (!Main.player[Main.myPlayer].male)
						//{
							SoundEngine.PlaySound(20, (int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, 1);
						//}
						//else
						//{
						//	SoundEngine.PlaySound(1, (int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, 1);
						//}
						CombatText.NewText(new Rectangle((int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, Main.player[Main.myPlayer].width, Main.player[Main.myPlayer].height), new Color(255, 80, 90, 255), string.Concat((int)damage + tempDmg), crit);
						if(Main.player[Main.myPlayer].statMana <= 0) return true;
					return false;
					}
				return true;
			}
			else if(skill80lev > 0 && statEnergyShield == 0)	
			{
				double tempDamage = Main.CalculateDamagePlayersTake(damage, Main.player[Main.myPlayer].statDefense);
				damage =  (int)tempDamage;
				Main.player[Main.myPlayer].statMana -= damage;
				//LetHurt = true;
				Main.player[Main.myPlayer].immune = true;
				Main.player[Main.myPlayer].immuneTime = 40;
				if(Main.player[Main.myPlayer].longInvince) Main.player[Main.myPlayer].immuneTime +=40;
				//if (!Main.player[Main.myPlayer].male)
				//{
					SoundEngine.PlaySound(20, (int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, 1);
				//}
				//else
				//{
				//	SoundEngine.PlaySound(1, (int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, 1);
				//}
				CombatText.NewText(new Rectangle((int)Main.player[Main.myPlayer].position.X, (int)Main.player[Main.myPlayer].position.Y, Main.player[Main.myPlayer].width, Main.player[Main.myPlayer].height), new Color(255, 80, 90, 255), string.Concat((int)damage), crit);
				if(Main.player[Main.myPlayer].statMana <= 0) return true;
				return false;
			}
			//LetHurt = false;
			return true;
			
		}
		
